import React from "react"
import { ToastContainer, toast } from 'react-toastify';

import 'react-toastify/dist/ReactToastify.min.css';

class Notifications extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			show: true,
		}
	}

    componentDidCatch(error, errorInfo) {
		mp.trigger("client:ui:debug", "Notifications.jsx", error, errorInfo) // eslint-disable-line
	}

    sendNotification(text) {
        toast.loading(text);
        toast.success(text);
        toast.info(text);
        toast.error(text);
        toast.warning(text);
        toast.done(text);
	}

    render() {
		if (!this.state.show) {
			return null
		}

		return (
			<React.Fragment>
                <div class="notifications">
                    {/* <div class="Toastify__toast Toastify__toast--success">
                        <div class="Toastify__toast-icon">
                        <svg viewBox="0 0 24 24">
                            <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z"></path>
                        </svg>
                        </div>
                        <div class="Toastify__toast-body">
                        <div>Успешно</div>
                        </div>
                    </div> */}
                    <div class="Toastify__toast Toastify__toast--error">
                        <div class="Toastify__toast-icon">
                        <svg viewBox="0 0 24 24">
                            <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z"></path>
                        </svg>
                        </div>
                        <div class="Toastify__toast-body">
                        <div>Ошибка</div>
                        </div>
                    </div>
                    <div class="Toastify__toast Toastify__toast--warning">
                        <div class="Toastify__toast-icon warning-badge">
                        <svg viewBox="0 0 24 24">
                            <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 15h-2v-2h2v2zm0-4h-2V7h2v6z"></path>
                        </svg>
                        </div>
                        <div class="Toastify__toast-body">
                        <div>Предупреждение</div>
                        </div>
                    </div>
                    <div class="Toastify__toast Toastify__toast--info">
                        <div class="Toastify__toast-icon">
                        <svg viewBox="0 0 24 24">
                            <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 15h-2v-6h2v6zm0-8h-2V7h2v2z"></path>
                        </svg>
                        </div>
                        <div class="Toastify__toast-body">
                        <div>Информация</div>
                        </div>
                    </div>
                </div>
            </React.Fragment>
        )
    }
}

export default Notifications;
