import React from 'react';

class AdminMenu extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			show: false
		}
	}

    componentDidMount() {
		EventManager.addHandler('adminmenu', (value) => {
			if (value.type === 'show') {
				this.setState({show: true})
			} else if (value.type === 'hide') {
				this.setState({show: false})
			}
		});
	}

    componentWillUnmount() {
		EventManager.removeHandler('adminmenu')
	}
    
    render() {
		if (!this.state.show) {
			return null
		}
        return (
            <div>
                
            </div>
        )
    }
}

export default AdminMenu;
